generateRandomString(length = 50) {
  const characters = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const charactersLength = characters.length;
  let randomString = '';
  for (let i = 0; i < length; i++) {
    const randomIndex = Math.floor(Math.random() * charactersLength);
    randomString += characters.charAt(randomIndex);
  }
  return randomString;
}


  encryptDataWithPublicKey(@Req() req:Request): string {
    const pgPublicKey =
      '';
    const public_key = `-----BEGIN PUBLIC KEY-----\n${pgPublicKey}\n-----END PUBLIC KEY-----`;
    const {
      merchantId,
      datetime,
      orderId,
      random,
    } = req.body

    const requestbody ={
      merchantId:merchantId ,
      datetime:datetime ,
      orderId: orderId,
      random:random 
    }
    const bufferData = Buffer.from(JSON.stringify(requestbody), 'utf8');
    const encryptedData = crypto.publicEncrypt(
      { key: public_key, padding: crypto.constants.RSA_PKCS1_PADDING },
      bufferData,
    );
    return encryptedData.toString('base64');
  }


  
  @Post('generateSignature')
 async generateSignature(@Req() req:Request) {
    try {
     const pgprivateKey =' '
      const mar_pri_key =`-----BEGIN PRIVATE KEY-----\n${pgprivateKey}\n-----END PRIVATE KEY-----`;
      const privateKey = crypto.createPrivateKey({
        key: mar_pri_key
      });
      const {
        merchantId,
        datetime,
        orderId,
        random,
      } = req.body
  
      const requestbody ={
        merchantId:merchantId ,
        datetime:datetime ,
        orderId: orderId,
        random:random 
      }
      // Convert the plain sensitive data to a string
      const plainSensitiveData = JSON.stringify(requestbody);
      // Create the signature using SHA1withRSA algorithm
      const signer = crypto.createSign('RSA-SHA1');
      signer.update(plainSensitiveData);
      const signature = signer.sign(privateKey, 'base64');
      return {generateSignature:signature};
    } catch (error) {
      console.error('Signature generation error:', error);
      throw new Error('Failed to generate the signature.');
    }
    
 }



 @Post('encode')
  encodeDataAndSignature(@Req() req:Request) {
    const encrypteddata = this.encryptDataWithPublicKey(req)
    const encodedSensitiveData = base64.encode(encrypteddata);
    const signaturei =this.generateSignature(req)
    const encodedSignature = base64.encode(signaturei);
    return {
      encodedSensitiveData,
      encodedSignature,
    };
  }

  @Post('decode')
  @Post('decode')
  async decodeDataAndSignature(@Body() reqBody: any) {
    const encodedSensitiveData = reqBody.encodedSensitiveData;
    const encodedSignature = reqBody.encodedSignature;

    try {
      const decodedSensitiveData = await Buffer.from(encodedSensitiveData, 'base64').toString('utf-8');
      const decodedSignature = await Buffer.from(encodedSignature, 'base64').toString('utf-8');

      return {
        decodedSensitiveData,
        decodedSignature,
      };
    } catch (error) {
      // Handle any errors that may occur during decoding
      console.error('Error while decoding:', error.message);
      throw new Error('Decoding error occurred.');
    }
  }



@Post('encrypt')
async encrypt(@Body() data:string) {
  const pgPublicKey ='';
const public_key = `-----BEGIN PUBLIC KEY-----\n${pgPublicKey}\n-----END PUBLIC KEY-----`;
  const encryptedData  = crypto.publicEncrypt(
    { key:public_key, padding: crypto.constants.RSA_PKCS1_PADDING },
    Buffer.from(JSON.stringify(data))
  );
  const encryptedString = encryptedData.toString('base64');
  return { encryptedData: encryptedString };
}

@Post('decrypt')
async decryptPayload(@Body() data:string){
    const pgprivateKey =''
    const mar_pri_key =`-----BEGIN PRIVATE KEY-----\n${pgprivateKey}\n-----END PRIVATE KEY-----`;
    const decrypted = crypto. privateDecrypt({ key: mar_pri_key, padding: crypto.constants.RSA_PKCS1_PADDING }, Buffer.from(JSON.stringify(data) ,'base64'));
    return decrypted.toString("utf8");
   
}


private getTimeStamp() {
  const timestamp = dayjs().tz('Asia/Dhaka').format('YYYYMMDDHHmmss');

  return timestamp;
}



